      <div>


    </div>

{/* modal for posting*/}
<Modal 
        open = {openP} //state to keep track if its open
        onClose={() => setOpenP(false)} //onClose method. closes the model when anywhere else on the screen is clicked
      >
        <div style={modalStyle} className={classes.paper}>
        <div className="imageupload">
         <form className= "app__signup">

              <progress className="imageupload__progress" value={progress} max="100"/>
            <input type="text" placeholder='Enter a caption...' onChange={event => setCaption(event.target.value)} value={caption}/>
            <button className={classes.button} onClick={handleOpen1} onChange={event => setKeyword(event.target.value)} value={keyword}> Open the select</button>
            <FormControl className={classes.formControl}>
        <InputLabel id="demo-controlled-open-select-label">Age</InputLabel>
        <Select labelId="demo-controlled-open-select-label" id="demo-controlled-open-select" open={open} onClose={handleClose} onOpen={handleOpen1} value={age} onChange={handleChange1}>
          <MenuItem value="">
